import Vue from 'vue'
import App from './App.vue'
import router from './router'

import'mint-ui/lib/style.css'

Vue.config.productionTip = false
//: 设置请求的根路径 
//Vue.http.options.root = "http://127.0.0.1/vue_ser/";
//:全局设置post 时候表音的数据组织格式为 application/x-www-form-urlencoded
//Vue.http.options.emulateJSON = true;
// 导入 MUI 的样式表， 和 Bootstrap 用法没有差别
import './lib/mui/css/mui.css'
// 导入 MUI 的样式表，扩展图标样式，购物车图标
// 还需要加载图标字体文件
import './lib/mui/css/icons-extra.css'

//1：引入imt-ui组件库
 //1):引入指定组件 Header
import {Header,Swipe,SwipeItem,Button} from "mint-ui"; //只将库中三个模块引进来
 //2):注册指定组件(注册一个组件)
Vue.component(Header.name,Header);

Vue.component(Swipe.name,Swipe);
Vue.component(SwipeItem.name,SwipeItem);
Vue.component(Button.name,Button);
 //3):在Home.vue使用组件


//2.引入axios库 main.js
import axios from "axios"  //将库中所有东西都引过来，所以不用加{}
  //1).修改配置信息 跨域保存session值
axios.defaults.withCredentials=true;
  //2).注册组件
Vue.prototype.axios=axios;

//3.创建全局 日期格式过滤器
  //2018-10-10 12:11:11
  Vue.filter("datatimeFilter",function(val){
  //val 当前日期对象
    // 1:新创建日期对象
    var data=new Date(val);
    // 2:获取年月日时分秒
    var y=data.getFullYear();
    var m=data.getMonth()+1;
    var d=data.getDate();
    var h=data.getHours();
    var min=data.getMinutes();  
    var s=data.getSeconds();
    // 3:拼接字符串
    m<10&&(m="0"+m);
    d<10&&(m="0"+d);
    return `${y}-${m}-${d} ${h}:${min}:${s}`;
  })
new Vue({
  router,
  render: h => h(App)
}).$mount('#app')
